// desktop menu
const email = document.querySelector(".navbar-email");
email.addEventListener("click", toggleMenu);

// mobile menu
const burgerMenu = document.querySelector(".menu");
burgerMenu.addEventListener("click", toggleMenu);

// shopping cart
const cartButton = document.querySelector(".navbar-shopping-cart");
cartButton.addEventListener("click", toggleMenu);

// product detail
const close = document.querySelector(".detail-close");
close.addEventListener("click", toggleMenu);

const components = [];
const mobileMenu = document.querySelector(".mobile-menu");
const desktopMenu = document.querySelector(".desktop-menu");
const shoppingCart = document.querySelector(".product-detail");
const detail = document.querySelector(".detail");
components.push(mobileMenu, desktopMenu, shoppingCart, detail);

function closeAll() {
  for (const component of components) {
    component.classList.add("toggleMenu");
  }
}

function openDetail(product) {
  console.log("click");
  closeAll();
  const productDetail = document.querySelector(".detail");
  productDetail.innerHTML = "";

  detail.classList.remove("toggleMenu");
  detail.innerHTML = `
  <div class="detail-close">
    <img class="close" src="./icons/icon_close.png" alt="close">
  </div>
  <img
    src="${product.image}"
    alt="${product.name}"
  />
  <div class="info">
    <p>$${product.price}</p>
    <p>${product.name}</p>
    <p>
      With its practical position, this bike also fulfills a decorative
      function, add your hall or workspace.
    </p>
    <button class="primary-button add-to-cart-button">
      <img src="./icons/bt_add_to_cart.svg" alt="add to cart" />
      Add to cart
    </button>
  </div>`;
  const close = document.querySelector(".detail-close");
  close.addEventListener("click", toggleMenu);

  const button = document.querySelector(".add-to-cart-button");
  button.addEventListener("click", () => {
    addToCart(product);
  });
}

//abrir y cerrar los menus
function toggleMenu(event) {
  console.log({ event });
  console.log(event.target.className);

  const mobileMenu = document.querySelector(".mobile-menu");
  const desktopMenu = document.querySelector(".desktop-menu");
  const shoppingCart = document.querySelector(".product-detail");
  const detail = document.querySelector(".detail");

  switch (event.target.className) {
    case "navbar-email":
      openOne(desktopMenu);
      break;

    case "menu":
      openOne(mobileMenu);
      break;

    case "carrito":
      openOne(shoppingCart);
      if (!desktopMenu.classList.contains("toggleMenu")) {
        desktopMenu.classList.add("toggleMenu");
      }
      break;

    case "close":
      detail.classList.add("toggleMenu");
      break;

    case "detail-close":
      detail.classList.add("toggleMenu");
      break;

    default:
      break;
  }

  function openOne(open) {
    for (const component of components) {
      if (!component.classList.contains("toggleMenu") && component != open) {
        component.classList.add("toggleMenu");
      }
    }
    open.classList.toggle("toggleMenu");
  }
}

//Add to cart
const myCart = [];
let myCheck = 0
function setCart(product, price) {
  if (typeof(product) === 'object'){
    myCart.push(product);
    myCheck += product.price;
  } else if (typeof(product) === 'string'){
    myCart.pop(Number(product) - 1);
    myCheck -= Number(price);
  }
  const cart = document.getElementById("cart");
  cart.innerText = myCart.length;

  if (myCart.length > 0) {
    cart.classList.add("carrito-background");
  } else {
    cart.innerText = ''
    cart.classList.remove("carrito-background");
  }

  const check = document.getElementById('check');
  check.innerText = myCheck
}

function addToCart(product) {
  setCart(product)
  const myOrder = document.querySelector('.my-order-content');

  const div = document.createElement('div');
  div.classList.add('shopping-cart');
  div.setAttribute('id', `${myCart.length}`);

  const figure = document.createElement('figure');

  const img = document.createElement('img');
  img.setAttribute('src', `${product.image}`);
  img.setAttribute('alt', `${product.name}`);

  const name = document.createElement('p');
  name.innerText = `${product.name}`;

  const price = document.createElement('p');
  price.innerText = `${product.price}`;

  const x = document.createElement('img');
  x.setAttribute('src', './icons/icon_close.png');
  x.setAttribute('alt', 'x');
  x.addEventListener("click", (event) => {deleteToCart(event, product)});

  figure.appendChild(img)
  div.append(figure, name, price, x)
  myOrder.appendChild(div);

}

function deleteToCart(event, product) {
  setCart(event.path[1].id, product.price)
  const item = document.getElementById(event.path[1].id);
  item.remove();
}

